function writeUserData(e,a,t,s,i){var n=firebase.database().ref().child("posts").push().key;firebase.database().ref("users/"+n).set({username:e,childname:a,email:t,message:s,created:i}).then(function(){alert("報名成功！"),console.log("username="+e+" / email="+t+"/ message="+s+" / today="+i+" key = "+n)}).catch(function(e){console.log("失敗了")})}function displayChatMessage(e,a){$("<div/>").text(a).prepend($("<em/>").text(e+": ")).appendTo($("#messagesDiv")),$("#messagesDiv")[0].scrollTop=$("#messagesDiv")[0].scrollHeight}function displayJoinMember(e){$("ul").prepend($("<li/>").text(e)).appendTo($("#sign_up")),$("#sign_up")[0].scrollTop=$("#sign_up")[0].scrollHeight}var db=firebase.database(),today=new Date,date=today.getFullYear()+"-"+(today.getMonth()+1)+"-"+today.getDate(),time=today.getHours()+":"+today.getMinutes()+":"+today.getSeconds(),dateTime=date+" "+time;$("#sendMsgButton").on("click",function(){var e=$("#username").val(),a=$("#childname").val();writeUserData(e,$("#email").val(),a,$("#message").val(),dateTime)});