function writeUserData(e,a,t,s,n,o){var i=firebase.database().ref().child("posts").push().key;firebase.database().ref("users/"+i).set({username:e,childname:a,email:t,phone:s,message:n,created:o}).then(function(){alert("報名成功！"),console.log("username="+e+" / email="+t+"/ message="+n+" / today="+o+" key = "+i)}).catch(function(e){alert("失敗了"),console.log("失敗了")})}function displayChatMessage(e,a){$("<div/>").text(a).prepend($("<em/>").text(e+": ")).appendTo($("#messagesDiv")),$("#messagesDiv")[0].scrollTop=$("#messagesDiv")[0].scrollHeight}function displayJoinMember(e){$("ul").prepend($("<li/>").text(e)).appendTo($("#sign_up")),$("#sign_up")[0].scrollTop=$("#sign_up")[0].scrollHeight}var db=firebase.database(),today=new Date,date=today.getFullYear()+"-"+(today.getMonth()+1)+"-"+today.getDate(),time=today.getHours()+":"+today.getMinutes()+":"+today.getSeconds(),dateTime=date+" "+time;$("#sendMsgButton").on("click",function(){writeUserData($("#username").val(),$("#childname").val(),$("#email").val(),$("#phone").val(),$("#message").val(),dateTime)});