function writeUserData(e,a,t,s){var o=firebase.database().ref().child("posts").push().key;firebase.database().ref("users/"+o).set({username:e,email:a,message:t,created:s}).then(function(){console.log("username="+e+" / email="+a+"/ message="+t+" / today="+s+" key = "+o)}).catch(function(e){console.log("失敗了")})}function displayChatMessage(e,a){$("<div/>").text(a).prepend($("<em/>").text(e+": ")).appendTo($("#messagesDiv")),$("#messagesDiv")[0].scrollTop=$("#messagesDiv")[0].scrollHeight}function displayJoinMember(e){$("ul").prepend($("<li/>").text(e)).appendTo($("#sign_up")),$("#sign_up")[0].scrollTop=$("#sign_up")[0].scrollHeight}var db=firebase.database(),today=new Date,date=today.getFullYear()+"-"+(today.getMonth()+1)+"-"+today.getDate(),time=today.getHours()+":"+today.getMinutes()+":"+today.getSeconds(),dateTime=date+" "+time;$("#sendMsgButton").on("click",function(){writeUserData($("#username").val(),$("#email").val(),$("#message").val(),dateTime)});